INSERT INTO `core_email_template` VALUES (1,'Amasty: Abandoned Cart Reminder','\n\n\n<body leftmargin=\"0\" marginwidth=\"0\" topmargin=\"0\" marginheight=\"0\" offset=\"0\">\n    <!-- change color styles for  backgr-color, bord_color and tr tag-->\n        <table class=\"main-table\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" height=\"100%\" width=\"100%\" style=\"font-family: \'Myriad Pro\', \'Verdana\', Geneva; font-size: 16pt;\">\n            <tr class=\"top\">\n                <td align=\"center\" valign=\"top\">\n                    <table class=\"top-table font_12\" border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"600\" style=\"width:600px; max-width: 600px;font-size: 12pt;font-family: \'Myriad Pro\', \'Verdana\', Geneva;\">\n                        <tr>\n                            <td align=\"left\"><img src=\"{{var logo_url}}\"/></td>\n                            <td align=\"right\">Phone: +123 456 789</td>\n                        </tr>\n                    </table>\n                </td>\n            </tr>\n            \n            <tr class=\"body\">\n                <td align=\"center\" valign=\"top\">\n                    <table class=\"container bord_color\" cellpadding=\"0\" cellspacing=\"0\" width=\"600\" style=\"width:600px; max-width: 600px;overflow: hidden; border-radius:12px; border: 1px solid #FFA453\">\n                        <tr class=\"email-head\">\n                            <td align=\"center\" valign=\"top\">\n                                <table class=\"table-head\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n                                    <tr width=\"100%\">\n                                        <td class=\"banner backgr-color\" align=\"center\" valign=\"top\" style=\"background-color: #FFA453; max-height: 76px;\"><div style=\"height: 76px;max-height: 76px;\"><img src=\"{{skin url=\"images/amacart/banner.png\" _area=\'frontend\'}}\" alt=\"\" /></div></td>\n                             